    { id: 6, name: 'Supreme', description: 'Pepperoni, sausage, mushrooms, peppers, and onions', price: 16.99, image: '/images/supreme.jpg' },
  ];

  return (
    <div className="modern-pizza-app">
      <Header />

      <main>
        <HeroSection />

        <FeaturesSection />

        <section className="featured-pizzas">
          <div className="container">
            <h2>Our Signature Pizzas</h2>
