import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function MyAccount() {
  const [view, setView] = useState('login'); // 'login' | 'register' | 'logout'
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    setIsLoggedIn(true);
    setView('logout');
  };

  const handleRegister = (e) => {
    e.preventDefault();
    setIsLoggedIn(true);
    setView('logout');
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setView('login');
  };

  return (
    <div className="account-page">
      <header className="header">
        <div className="logo">
          <Link to="/">Pizza Website</Link>
        </div>
        <nav className="nav">
          <Link to="/">Home</Link>
          <Link to="/menu">Menu</Link>
          <Link to="/cart">Cart</Link>
          <Link to="/myaccount">My Account</Link>
        </nav>
      </header>
      <main>
        <h2>My Account</h2>
        {view === 'login' && (
          <form className="account-form" onSubmit={handleLogin}>
            <h3>Login</h3>
            <input type="email" placeholder="Email" required />
            <input type="password" placeholder="Password" required />
            <button type="submit">Login</button>
            <p>
              New user?{' '}
              <button type="button" onClick={() => setView('register')}>
                Register here
              </button>
            </p>
          </form>
        )}
        {view === 'register' && (
          <form className="account-form" onSubmit={handleRegister}>
            <h3>Register</h3>
            <input type="text" placeholder="Name" required />
            <input type="email" placeholder="Email" required />
            <input type="password" placeholder="Password" required />
            <button type="submit">Register</button>
            <p>
              Already have an account?{' '}
              <button type="button" onClick={() => setView('login')}>
                Login here
              </button>
            </p>
          </form>
        )}
        {view === 'logout' && isLoggedIn && (
          <div>
            <h3>Welcome!</h3>
            <button onClick={handleLogout}>Logout</button>
          </div>
        )}
      </main>
      <style>{`
        .account-page { max-width: 400px; margin: 40px auto; padding: 24px; background: #fff; border-radius: 12px; box-shadow: 0 2px 12px rgba(0,0,0,0.08);}
        .account-form { display: flex; flex-direction: column; gap: 12px; }
        .account-form input { padding: 8px; border-radius: 6px; border: 1px solid #ccc; }
        .account-form button { background: #e74c3c; color: #fff; border: none; padding: 10px; border-radius: 6px; cursor: pointer; }
        .account-form h3 { margin-bottom: 8px; }
        .header { background: #ff6347; color: #fff; padding: 1rem 2rem; display: flex; align-items: center; justify-content: space-between; font-family: 'Poppins', sans-serif; }
        .logo { font-size: 1.5rem; font-weight: bold; }
        .nav { display: flex; gap: 1.5rem; }
        .nav a { color: #fff; text-decoration: none; font-size: 1rem; transition: color 0.2s; }
        .nav a:hover { color: #ffd700; }
      `}</style>
    </div>
  );
}

export default MyAccount;
