import React, { useState, useEffect } from 'react';
import { useCart } from './CartContext';

function PizzaCard({ pizza, onAddToCart }) {
  const [isHovered, setIsHovered] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);

  return (
    <div 
      className={`pizza-card ${isHovered ? 'hovered' : ''}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="pizza-image-container">
        {!imageLoaded && <div className="image-placeholder"></div>}
        <img 
          src={pizza.img} 
          alt={pizza.name}
          onLoad={() => setImageLoaded(true)}
          className={imageLoaded ? 'loaded' : ''}
        />
        <div className="pizza-overlay">
          <button 
            className="quick-view-btn"
            onClick={() => console.log(`Quick view: ${pizza.name}`)}
          >
            👁️ Quick View
          </button>
        </div>
        {pizza.isPopular && <div className="popular-badge">🔥 Popular</div>}
      </div>

      <div className="pizza-info">
        <h3>{pizza.name}</h3>
        <p className="pizza-description">{pizza.description}</p>

        <div className="pizza-details">
          <div className="rating">
            {'★'.repeat(Math.floor(pizza.rating))}
            {'☆'.repeat(5 - Math.floor(pizza.rating))}
            <span>({pizza.reviews})</span>
          </div>

          <div className="tags">
            {pizza.tags.map((tag, index) => (
              <span key={index} className="tag">{tag}</span>
            ))}
          </div>
        </div>

        <div className="price-add-container">
          <div className="price">
            <span className="current-price">₹{pizza.price}</span>
            {pizza.originalPrice && (
              <span className="original-price">₹{pizza.originalPrice}</span>
            )}
          </div>
          <button 
            className="add-to-cart-btn"
            onClick={() => onAddToCart(pizza)}
          >
            <span>Add to Cart</span>
            <span className="cart-icon">+</span>
          </button>
        </div>
      </div>
    </div>
  );
}

function Menu() {
  const { addToCart } = useCart();
  const [pizzas, setPizzas] = useState([]);
  const [filteredPizzas, setFilteredPizzas] = useState([]);
  const [activeFilter, setActiveFilter] = useState('all');
  const [sortBy, setSortBy] = useState('popular');
  const [searchQuery, setSearchQuery] = useState('');

  // Pizza categories
  const categories = [
    { id: 'all', name: 'All Pizzas' },
    { id: 'vegetarian', name: 'Vegetarian' },
    { id: 'non-vegetarian', name: 'Non-Vegetarian' },
    { id: 'premium', name: 'Premium' },
    { id: 'classic', name: 'Classic' },
    { id: 'new', name: 'New Arrivals' }
  ];

  // Sort options
  const sortOptions = [
    { id: 'popular', name: 'Most Popular' },
    { id: 'price-low', name: 'Price: Low to High' },
    { id: 'price-high', name: 'Price: High to Low' },
    { id: 'rating', name: 'Highest Rated' },
    { id: 'name', name: 'Name A-Z' }
  ];

  // Load pizza data
  useEffect(() => {
    const pizzaData = [
      { 
        id: 1, 
        name: "Margherita", 
        price: 299, 
        originalPrice: 349,
        img: "https://images.unsplash.com/photo-1604068549290-dea0e4a305ca?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80", 
        description: "Classic delight with 100% real mozzarella cheese",
        rating: 4.5,
        reviews: 128,
        tags: ["Vegetarian", "Cheesy"],
        category: "vegetarian",
        isPopular: true
      },
      { 
        id: 2, 
        name: "Pepperoni", 
        price: 349, 
        img: "https://images.unsplash.com/photo-1628840042765-356cda07504e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80", 
        description: "Spicy pepperoni with extra cheese",
        rating: 4.7,
        reviews: 95,
        tags: ["Non-Vegetarian", "Spicy"],
        category: "non-vegetarian",
        isPopular: true
      },
      { 
        id: 3, 
        name: "Veggie Paradise", 
        price: 329, 
        img: "https://images.unsplash.com/photo-1593560708920-61dd98c46a4e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80", 
        description: "Golden corn, black olives, capsicum, and red paprika",
        rating: 4.3,
        reviews: 76,
        tags: ["Vegetarian", "Healthy"],
        category: "vegetarian"
      },
      { 
        id: 4, 
        name: "Paneer Tikka", 
        price: 379, 
        img: "https://images.unsplash.com/photo-1601924582970-9238bcb495d9?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80", 
        description: "Flavorful paneer with spicy tikka masala",
        rating: 4.6,
        reviews: 112,
        tags: ["Vegetarian", "Spicy"],
        category: "vegetarian",
        isPopular: true
      },
      { 
        id: 5, 
        name: "Chicken Supreme", 
        price: 429, 
        img: "https://images.unsplash.com/photo-1604382355076-af4b0eb60143?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80", 
        description: "Chicken sausage, chicken pepperoni, and grilled chicken",
        rating: 4.8,
        reviews: 88,
        tags: ["Non-Vegetarian", "Premium"],
        category: "non-vegetarian"
      },
      { 
        id: 6, 
        name: "Farmhouse", 
        price: 359, 
        img: "https://images.unsplash.com/photo-1571407970349-bc81e7e96d47?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80", 
        description: "Capsicum, mushrooms, tomatoes, and onions",
        rating: 4.4,
        reviews: 64,
        tags: ["Vegetarian", "Classic"],
        category: "vegetarian"
      },
      { 
        id: 7, 
        name: "BBQ Chicken", 
        price: 449, 
        img: "https://images.unsplash.com/photo-1595708684082-a173bb3a06c5?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80", 
        description: "Grilled chicken with BBQ sauce and onions",
        rating: 4.9,
        reviews: 102,
        tags: ["Non-Vegetarian", "Premium"],
        category: "non-vegetarian",
        isPopular: true
      },
      { 
        id: 8, 
        name: "Truffle Mushroom", 
        price: 499, 
        img: "https://images.unsplash.com/photo-1513104890138-7c749659a591?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80", 
        description: "Gourmet pizza with truffle oil and assorted mushrooms",
        rating: 4.7,
        reviews: 57,
        tags: ["Vegetarian", "Premium", "New"],
        category: "premium",
        isNew: true
      }
    ];

    setPizzas(pizzaData);
    setFilteredPizzas(pizzaData);
  }, []);

  // Filter and sort pizzas
  useEffect(() => {
    let result = [...pizzas];

    // Apply category filter
    if (activeFilter !== 'all') {
      result = result.filter(pizza => 
        pizza.category === activeFilter || 
        (activeFilter === 'new' && pizza.isNew) ||
        (activeFilter === 'premium' && pizza.tags.includes('Premium'))
      );
    }

    // Apply search filter
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      result = result.filter(pizza => 
        pizza.name.toLowerCase().includes(query) ||
        pizza.description.toLowerCase().includes(query) ||
        pizza.tags.some(tag => tag.toLowerCase().includes(query))
      );
    }

    // Apply sorting
    switch(sortBy) {
      case 'price-low':
        result.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        result.sort((a, b) => b.price - a.price);
        break;
      case 'rating':
        result.sort((a, b) => b.rating - a.rating);
        break;
      case 'name':
        result.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case 'popular':
      default:
        // Popular items first, then by rating
        result.sort((a, b) => {
          if (a.isPopular && !b.isPopular) return -1;
          if (!a.isPopular && b.isPopular) return 1;
          return b.rating - a.rating;
        });
    }

    setFilteredPizzas(result);
  }, [pizzas, activeFilter, sortBy, searchQuery]);

  const handleAddToCart = (pizza) => {
    addToCart(pizza);
    // Show notification or animation
    const event = new CustomEvent('cartNotification', { 
      detail: { message: `${pizza.name} added to cart!` } 
    });
    window.dispatchEvent(event);
  };

  return (
    <div className="menu-page">
      <div className="menu-header">
        <h1>Our Delicious Pizzas</h1>
        <p>Handcrafted with the finest ingredients and baked to perfection</p>
      </div>

      <div className="menu-controls">
        <div className="search-box">
          <input
            type="text"
            placeholder="Search pizzas..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <span className="search-icon">🔍</span>
        </div>

        <div className="filters">
          <div className="category-filters">
            {categories.map(category => (
              <button
                key={category.id}
                className={activeFilter === category.id ? 'active' : ''}
                onClick={() => setActiveFilter(category.id)}
              >
                {category.name}
              </button>
            ))}
          </div>

          <div className="sort-filter">
            <label>Sort by:</label>
            <select 
              value={sortBy} 
              onChange={(e) => setSortBy(e.target.value)}
            >
              {sortOptions.map(option => (
                <option key={option.id} value={option.id}>
                  {option.name}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      <div className="pizza-grid">
        {filteredPizzas.length > 0 ? (
          filteredPizzas.map(pizza => (
            <PizzaCard 
              key={pizza.id} 
              pizza={pizza} 
              onAddToCart={handleAddToCart}
            />
          ))
        ) : (
          <div className="no-results">
            <h3>No pizzas found</h3>
            <p>Try a different search or filter</p>
          </div>
        )}
      </div>

      <style jsx>{`
