import React from 'react';

function PizzaCard({ name, price, img }) {
  return (
    <div className="pizza-card">
      <img src={img} alt={name} />
      <h3>{name}</h3>
      <p>â‚¹{price}</p>
      <button className="button">Add to Cart</button>
    </div>
  );
}

function Menu() {
  const pizzas = [
    { name: "Margherita", price: 299, img: "https://images.unsplash.com/photo-1601924579440-6f5f6b8b8b8b?auto=format&fit=crop&w=400&q=80" },
    { name: "Pepperoni", price: 349, img: "https://images.unsplash.com/photo-1548365328-8b6b7b8b8b8b?auto=format&fit=crop&w=400&q=80" },
    { name: "Veggie", price: 279, img: "https://images.unsplash.com/photo-1513104890138-7c749659a591?auto=format&fit=crop&w=400&q=80" },
    { name: "Paneer Tikka", price: 329, img: "https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80" }
  ];

  return (
    <div className="container main-content">
      <h2>Our Menu</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'center' }}>
        {pizzas.map((pizza, idx) => (
          <PizzaCard key={idx} {...pizza} />
        ))}
      </div>
    </div>
  );
}

export default Menu;
