import React from 'react';
import PizzaCard from '../components/PizzaCard';

const pizzas = [
    {
        id: 1,
        name: 'Margherita',
        description: 'Classic pizza with fresh mozzarella, tomatoes, and basil.',
        price: 10.99,
        image: '/images/margherita.jpg'
    },
    {
        id: 2,
        name: 'Pepperoni',
        description: 'Delicious pepperoni slices on a bed of mozzarella cheese.',
        price: 12.99,
        image: '/images/pepperoni.jpg'
    },
    {
        id: 3,
        name: 'BBQ Chicken',
        description: 'Grilled chicken, BBQ sauce, and red onions on a cheesy base.',
        price: 13.99,
        image: '/images/bbq-chicken.jpg'
    },
    {
        id: 4,
        name: 'Veggie',
        description: 'A mix of fresh vegetables on a mozzarella base.',
        price: 11.99,
        image: '/images/veggie.jpg'
    }
];

const Menu = () => {
    return (
        <div className="menu">
            <h1>Our Menu</h1>
            <div className="pizza-list">
                {pizzas.map(pizza => (
                    <PizzaCard key={pizza.id} pizza={pizza} />
                ))}
            </div>
        </div>
    );
};

export default Menu;
