import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import { FiSearch, FiFilter, FiStar, FiShoppingCart, FiX, FiChevronLeft, FiPlus, FiMinus } from 'react-icons/fi';

function PizzaCard({ pizza, onViewDetails, onAddToCart }) {
  return (
    <div className="pizza-card">
      <div className="pizza-image-container">
        <img src={pizza.img} alt={pizza.name} />
        {pizza.isPopular && <span className="popular-badge">Popular</span>}
        {pizza.isNew && <span className="new-badge">New</span>}
        <div className="pizza-overlay">
          <button className="quick-view-btn" onClick={() => onViewDetails(pizza)}>
            Quick View
          </button>
        </div>
      </div>
      <div className="pizza-info">
        <h3>{pizza.name}</h3>
        <p className="pizza-description">{pizza.description}</p>

        <div className="pizza-details">
          <div className="rating">
            <FiStar className="star-icon" />
            <span>{pizza.rating || 4.5}</span>
            <span>({pizza.reviews || 50})</span>
          </div>

          <div className="tags">
            {pizza.tags?.map((tag, index) => (
              <span key={index} className="tag">{tag}</span>
            ))}
          </div>
        </div>

        <div className="price-add-container">
          <div className="price">
            <span className="current-price">₹{pizza.price}</span>
          </div>
          <button className="add-to-cart-btn" onClick={() => onAddToCart(pizza)}>
            <FiShoppingCart className="cart-icon" />
            Add
          </button>
        </div>
      </div>
    </div>
  );
}

function PizzaDetails({ pizza, onClose, onAddToCart, isOpen }) {
  const [quantity, setQuantity] = useState(1);

  const handleAddToCart = () => {
    for (let i = 0; i < quantity; i++) {
      onAddToCart(pizza);
    }
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="pizza-details-modal">
      <div className="modal-overlay" onClick={onClose}></div>
      <div className="modal-content">
        <button className="close-btn" onClick={onClose}>
          <FiX />
        </button>

        <div className="modal-body">
          <div className="modal-image">
            <img src={pizza.img} alt={pizza.name} />
          </div>

          <div className="modal-info">
            <h2>{pizza.name}</h2>
            <div className="modal-rating">
              <FiStar className="star-icon" />
              <span>{pizza.rating || 4.5}</span>
              <span>({pizza.reviews || 50} reviews)</span>
            </div>

            <p className="modal-description">{pizza.description}</p>

            <div className="modal-tags">
              {pizza.tags?.map((tag, index) => (
                <span key={index} className="tag">{tag}</span>
              ))}
            </div>

            <div className="quantity-selector">
              <span>Quantity:</span>
              <div className="quantity-controls">
                <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>
                  <FiMinus />
                </button>
                <span>{quantity}</span>
                <button onClick={() => setQuantity(quantity + 1)}>
                  <FiPlus />
                </button>
              </div>
            </div>

            <div className="modal-price">₹{pizza.price * quantity}</div>

            <button className="add-to-cart-large" onClick={handleAddToCart}>
              <FiShoppingCart />
              Add {quantity} to Cart
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

function Menu() {
  const { addToCart } = useCart();
  const [pizzas, setPizzas] = useState([]);
  const [selectedPizza, setSelectedPizza] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [sortBy, setSortBy] = useState('default');

  useEffect(() => {
    // Simulate API call
    setPizzas([
      {
        id: 1,
        name: "Margherita",
        price: 299,
        img: "https://images.unsplash.com/photo-1604068549290-dea0e4a305ca?auto=format&fit=crop&w=400&q=80",
        description: "Classic delight with 100% real mozzarella cheese",
        rating: 4.5,
