import React from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import PizzaCard from '../components/PizzaCard';
import '../styles/main.css';

function Home() {
  const featuredPizzas = [
    { id: 1, name: 'Margherita', description: 'Classic pizza with fresh mozzarella and basil', price: 12.99, image: '/images/margherita.jpg' },
    { id: 2, name: 'Pepperoni', description: 'Spicy pepperoni with mozzarella cheese', price: 14.99, image: '/images/pepperoni.jpg' },
    { id: 3, name: 'BBQ Chicken', description: 'Grilled chicken with BBQ sauce and red onions', price: 15.99, image: '/images/bbq-chicken.jpg' },
  ];

  return (
    <div>
      <Header />
      <main>
        <div className="container main-content">
          <h2>Welcome to Pizza Website</h2>
          <p>Order your favorite pizza now!</p>
        </div>
        <h2>Featured Pizzas</h2>
        <div className="pizza-list">
          {featuredPizzas.map(pizza => (
            <PizzaCard key={pizza.id} pizza={pizza} />
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default Home;
