import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import PizzaCard from './PizzaCard'; // adjust path if needed

const pizzas = [
  {
    id: 1,
    name: "Margherita",
    description: "Classic delight with 100% real mozzarella cheese",
    price: 249,
    image: "https://images.unsplash.com/photo-1513104890138-7c749659a591?auto=format&fit=crop&w=400&q=80"
  },
  {
    id: 2,
    name: "Pepperoni",
    description: "Spicy pepperoni with extra cheese",
    price: 299,
    image: "https://images.unsplash.com/photo-1548365328-8b6b7b8b8b8b?auto=format&fit=crop&w=400&q=80"
  },
  {
    id: 3,
    name: "Veggie Paradise",
    description: "Golden corn, black olives, capsicum, and red paprika",
    price: 279,
    image: "https://images.unsplash.com/photo-1593560708920-61dd98c46a4e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80"
  },
  {
    id: 4,
    name: "Paneer Tikka",
    description: "Flavorful paneer with spicy tikka masala",
    price: 299,
    image: "https://images.unsplash.com/photo-1601924582970-9238bcb495d9?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80"
  },
  {
    id: 5,
    name: "Chicken Supreme",
    description: "Chicken sausage, chicken pepperoni, and grilled chicken",
    price: 349,
    image: "https://images.unsplash.com/photo-1604382355076-af4b0eb60143?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80"
  },
  {
    id: 6,
    name: "Farmhouse",
    description: "Capsicum, mushrooms, tomatoes, and onions",
    price: 279,
    image: "https://images.unsplash.com/photo-1571407970349-bc81e7e96d47?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80"
  },
  {
    id: 7,
    name: "BBQ Chicken",
    description: "Grilled chicken with BBQ sauce and onions",
    price: 399,
    image: "https://images.unsplash.com/photo-1595708684082-a173bb3a06c5?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80"
  },
  {
    id: 8,
    name: "Truffle Mushroom",
    description: "Gourmet pizza with truffle oil and assorted mushrooms",
    price: 499,
    image: "https://images.unsplash.com/photo-1513104890138-7c749659a591?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=400&q=80"
  }
];

function Menu() {
  const { addToCart } = useCart();
  const [filteredPizzas, setFilteredPizzas] = useState([]);
  const [activeFilter, setActiveFilter] = useState('all');
  const [sortBy, setSortBy] = useState('popular');
  const [searchQuery, setSearchQuery] = useState('');

  // Pizza categories
  const categories = [
    { id: 'all', name: 'All Pizzas' },
    { id: 'vegetarian', name: 'Vegetarian' },
    { id: 'non-vegetarian', name: 'Non-Vegetarian' },
    { id: 'premium', name: 'Premium' },
    { id: 'classic', name: 'Classic' },
    { id: 'new', name: 'New Arrivals' }
  ];

  // Sort options
  const sortOptions = [
    { id: 'popular', name: 'Most Popular' },
    { id: 'price-low', name: 'Price: Low to High' },
    { id: 'price-high', name: 'Price: High to Low' },
    { id: 'rating', name: 'Highest Rated' },
    { id: 'name', name: 'Name A-Z' }
  ];

  // Load pizza data
  useEffect(() => {
    setFilteredPizzas(pizzas);
  }, []);

  // Filter and sort pizzas
  useEffect(() => {
    let result = [...pizzas];

    // Apply category filter
    if (activeFilter !== 'all') {
      result = result.filter(pizza => 
        pizza.category === activeFilter || 
        (activeFilter === 'new' && pizza.isNew) ||
        (activeFilter === 'premium' && pizza.tags.includes('Premium'))
      );
    }

    // Apply search filter
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      result = result.filter(pizza => 
        pizza.name.toLowerCase().includes(query) ||
        pizza.description.toLowerCase().includes(query) ||
        pizza.tags.some(tag => tag.toLowerCase().includes(query))
      );
    }

    // Apply sorting
    switch(sortBy) {
      case 'price-low':
        result.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        result.sort((a, b) => b.price - a.price);
        break;
      case 'rating':
        result.sort((a, b) => b.rating - a.rating);
        break;
      case 'name':
        result.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case 'popular':
      default:
        // Popular items first, then by rating
        result.sort((a, b) => {
          if (a.isPopular && !b.isPopular) return -1;
          if (!a.isPopular && b.isPopular) return 1;
          return b.rating - a.rating;
        });
    }

    setFilteredPizzas(result);
  }, [activeFilter, sortBy, searchQuery]);

