  .footer-section a:hover {
    color: #e74c3c;
  }

  .social-icons {
    display: flex;
    gap: 1rem;
  }

  .social-icons a {
    font-size: 1.5rem;
  }

  .footer-bottom {
    text-align: center;
    padding-top: 2rem;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
