    background-color: transparent;
    color: white;
    border: 2px solid white;
  }

  .btn-secondary:hover {
    background-color: white;
    color: #e74c3c;
    transform: translateY(-3px);
  }

  /* Responsive Design */
  @media (max-width: 992px) {
    .video-content h1 {
      font-size: 2.5rem;
    }

    .video-content p {
      font-size: 1.2rem;
    }

    .process-visual {
      flex-direction: column;
    }
  }

  @media (max-width: 768px) {
    .nav {
      position: fixed;
      top: 0;
      right: -100%;
      height: 100vh;
      width: 70%;
      background: white;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      transition: right 0.3s ease;
      box-shadow: -5px 0 15px rgba(0, 0, 0, 0.1);
    }

    .nav-open {
      right: 0;
    }

    .mobile-menu-toggle {
      display: flex;
