    .newsletter-form {
      flex-direction: column;
    }

    .newsletter-form input {
      border-radius: 30px;
      margin-bottom: 1rem;
    }

    .newsletter-form button {
      border-radius: 30px;
    }

    .video-content {
      width: 90%;
    }

    .video-content h1 {
      font-size: 2rem;
    }
  }
`;

