import React, { useState, useEffect, useRef } from 'react';

// Modern Header Component
const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="container">
        <div className="logo">
          <span className="logo-icon">🍕</span>
          <span className="logo-text">Pizza Paradise</span>
        </div>

        <nav className={`nav ${isMobileMenuOpen ? 'nav-open' : ''}`}>
          <a href="#home">Home</a>
          <a href="#menu">Menu</a>
          <a href="#about">About</a>
          <a href="#contact">Contact</a>
          <button className="cta-button">Order Now</button>
        </nav>

        <button 
          className="mobile-menu-toggle"
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        >
          <span></span>
          <span></span>
          <span></span>
        </button>
      </div>
    </header>
  );
};

// Modern Pizza Card Component
const PizzaCard = ({ pizza }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="pizza-card"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="pizza-image-container">
        <img src={pizza.image} alt={pizza.name} className="pizza-image" />
        <div className={`pizza-overlay ${isHovered ? 'overlay-visible' : ''}`}>
          <button className="quick-add-btn">Add to Cart</button>
        </div>
      </div>
      <div className="pizza-info">
        <h3>{pizza.name}</h3>
        <p>{pizza.description}</p>
        <div className="price-add">
          <span className="price">${pizza.price.toFixed(2)}</span>
          <button className="add-button">+</button>
        </div>
      </div>
    </div>
  );
};

// Modern Footer Component
const Footer = () => {
  return (
    <footer className="footer">
      <div className="container">
        <div className="footer-content">
          <div className="footer-section">
            <h3>Pizza Paradise</h3>
            <p>The best pizza in town, made with love and tradition since 1995.</p>
          </div>

          <div className="footer-section">
            <h4>Quick Links</h4>
            <ul>
              <li><a href="#menu">Menu</a></li>
              <li><a href="#about">About Us</a></li>
              <li><a href="#contact">Contact</a></li>
              <li><a href="#careers">Careers</a></li>
            </ul>
          </div>

          <div className="footer-section">
            <h4>Contact Us</h4>
            <p>123 Pizza Street, Food City</p>
            <p>Phone: (123) 456-7890</p>
            <p>Email: info@pizzaparadise.com</p>
          </div>

          <div className="footer-section">
            <h4>Follow Us</h4>
            <div className="social-icons">
              <a href="#" aria-label="Facebook">📘</a>
              <a href="#" aria-label="Instagram">📸</a>
              <a href="#" aria-label="Twitter">🐦</a>
            </div>
          </div>
        </div>

        <div className="footer-bottom">
          <p>&copy; {new Date().getFullYear()} Pizza Paradise. All rights reserved.</p>
        </div>
      </div>
    </footer>
  );
};

// Video Hero Section Component
const VideoHeroSection = () => {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(true);

  return (
    <section className="video-hero">
      <div className="video-container">
        <video
          ref={videoRef}
          autoPlay
          loop
          muted={isMuted}
          playsInline
          className="hero-video"
        >
          <source src="https://assets.mixkit.co/videos/preview/mixkit-spinning-pizza-30955-large.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        <div className="video-overlay"></div>

        <div className="video-content">
          <h1>Authentic Italian Pizza</h1>
          <p>Handcrafted with premium ingredients and baked to perfection</p>
          <div className="hero-buttons">
            <button className="btn-primary">Order Now</button>
            <button className="btn-secondary">View Menu</button>
          </div>
        </div>

        <button 
          className="mute-toggle"
          onClick={() => setIsMuted(!isMuted)}
        >
          {isMuted ? '🔇' : '🔊'}
        </button>
      </div>
    </section>
  );
};

// Features Section Component
const FeaturesSection = () => {
  const features = [
    { icon: '🚀', title: 'Fast Delivery', description: 'Hot pizza delivered in 30 minutes or less' },
    { icon: '👨‍🍳', title: 'Expert Chefs', description: 'Our pizzaiolos have decades of experience' },
    { icon: '🌱', title: 'Fresh Ingredients', description: 'Locally sourced, organic ingredients' },
    { icon: '🔥', title: 'Wood-Fired Oven', description: 'Traditional cooking for authentic flavor' }
  ];

  return (
    <section className="features">
      <div className="container">
        <h2>Why Choose Us</h2>
        <p className="section-subtitle">We're committed to delivering the best pizza experience</p>
        <div className="features-grid">
          {features.map((feature, index) => (
            <div key={index} className="feature-card">
              <div className="feature-icon">{feature.icon}</div>
              <h3>{feature.title}</h3>
              <p>{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Testimonials Section Component
const TestimonialsSection = () => {
  const testimonials = [
    { 
      id: 1, 
      name: 'Sarah Johnson', 
      text: 'The best pizza I\'ve ever had! The crust is perfectly crispy and the ingredients are always fresh.', 
      rating: 5,
      image: '/images/customer1.jpg'
    },
    { 
      id: 2, 
      name: 'Mike Thompson', 
      text: 'Their delivery is always on time and the pizza arrives hot and delicious. Highly recommended!', 
      rating: 5,
      image: '/images/customer2.jpg'
    },
    { 
      id: 3, 
      name: 'Emily Rodriguez', 
      text: 'The variety of toppings and the quality of ingredients make Pizza Paradise my go-to pizza place.', 
      rating: 4,
      image: '/images/customer3.jpg'
    }
  ];

  return (
    <section className="testimonials">
      <div className="container">
        <h2>What Our Customers Say</h2>
        <p className="section-subtitle">Don't just take our word for it</p>

        <div className="testimonials-grid">
          {testimonials.map(testimonial => (
            <div key={testimonial.id} className="testimonial-card">
              <div className="testimonial-header">
                <div className="customer-image">
                  <img src={testimonial.image} alt={testimonial.name} />
                </div>
                <div className="customer-info">
                  <h4>{testimonial.name}</h4>
                  <div className="rating">
                    {'★'.repeat(testimonial.rating)}{'☆'.repeat(5 - testimonial.rating)}
                  </div>
                </div>
              </div>
              <p>"{testimonial.text}"</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Process Section Component
const ProcessSection = () => {
  const steps = [
    { number: '01', title: 'Choose Your Pizza', description: 'Select from our menu or create your own custom pizza with fresh ingredients.' },
    { number: '02', title: 'We Prepare It', description: 'Our expert chefs handcraft your pizza with care using traditional methods.' },
    { number: '03', title: 'Bake to Perfection', description: 'Your pizza is baked in our wood-fired oven for that authentic flavor.' },
    { number: '04', title: 'Fast Delivery', description: 'We deliver your hot, fresh pizza right to your door in record time.' }
  ];

  return (
    <section className="process">
      <div className="container">
        <h2>How It Works</h2>
        <p className="section-subtitle">From our kitchen to your door in simple steps</p>

        <div className="process-steps">
          {steps.map((step, index) => (
            <div key={index} className="process-step">
              <div className="step-number">{step.number}</div>
              <h3>{step.title}</h3>
              <p>{step.description}</p>
            </div>
          ))}
        </div>

        <div className="process-visual">
          <div className="pizza-making">
            <div className="chef-image">
              <img src="/images/pizza-chef.jpg" alt="Pizza chef preparing dough" />
            </div>
            <div className="oven-image">
              <img src="/images/pizza-oven.jpg" alt="Wood-fired pizza oven" />
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

// Instagram Section Component
const InstagramSection = () => {
  const instagramPosts = [
    { id: 1, image: '/images/instagram1.jpg', likes: '2,345', comments: '128' },
    { id: 2, image: '/images/instagram2.jpg', likes: '1,876', comments: '94' },
    { id: 3, image: '/images/instagram3.jpg', likes: '3,210', comments: '201' },
    { id: 4, image: '/images/instagram4.jpg', likes: '2,567', comments: '156' },
    { id: 5, image: '/images/instagram5.jpg', likes: '1,943', comments: '112' },
    { id: 6, image: '/images/instagram6.jpg', likes: '2,801', comments: '178' }
  ];

  return (
    <section className="instagram">
      <div className="container">
        <h2>Follow Us on Instagram</h2>
        <p className="section-subtitle">@PizzaParadise #PizzaLovers</p>

        <div className="instagram-grid">
          {instagramPosts.map(post => (
            <div key={post.id} className="instagram-post">
              <img src={post.image} alt="Instagram post" />
              <div className="post-overlay">
                <span>❤️ {post.likes}</span>
                <span>💬 {post.comments}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Main Home Component
function Home() {
  const featuredPizzas = [
    { id: 1, name: 'Margherita', description: 'Classic pizza with fresh mozzarella and basil', price: 12.99, image: '/images/margherita.jpg' },
    { id: 2, name: 'Pepperoni', description: 'Spicy pepperoni with mozzarella cheese', price: 14.99, image: '/images/pepperoni.jpg' },
    { id: 3, name: 'BBQ Chicken', description: 'Grilled chicken with BBQ sauce and red onions', price: 15.99, image: '/images/bbq-chicken.jpg' },
    { id: 4, name: 'Vegetarian', description: 'Fresh vegetables and mozzarella on tomato sauce', price: 13.99, image: '/images/vegetarian.jpg' },
    { id: 5, name: 'Hawaiian', description: 'Ham, pineapple, and mozzarella cheese', price: 14.99, image: '/images/hawaiian.jpg' },
    { id: 6, name: 'Supreme', description: 'Pepperoni, sausage, mushrooms, peppers, and onions', price: 16.99, image: '/images/supreme.jpg' },
  ];

  return (
    <div className="modern-pizza-app">
      <Header />

      <main>
        <VideoHeroSection />

        <FeaturesSection />

        <section className="featured-pizzas">
          <div className="container">
            <h2>Our Signature Pizzas</h2>
            <p className="section-subtitle">Handcrafted with the finest ingredients</p>

            <div className="pizza-grid">
              {featuredPizzas.map(pizza => (
                <PizzaCard key={pizza.id} pizza={pizza} />
              ))}
            </div>

            <div className="view-all-container">
              <button className="view-all-btn">View Full Menu</button>
            </div>
          </div>
        </section>

        <ProcessSection />

        <TestimonialsSection />

        <InstagramSection />

        <section className="newsletter">
          <div className="container">
            <h2>Get Special Offers</h2>
            <p>Subscribe to our newsletter for exclusive deals and new flavor announcements</p>
            <div className="newsletter-form">
              <input type="email" placeholder="Enter your email" />
              <button>Subscribe</button>
            </div>
          </div>
        </section>
      </main>

      <Footer />
    </div>
  );
}

export default Home;

// Modern CSS Styles
