    color: #2c3e50;
  }

  .section-subtitle {
    text-align: center;
    color: #7f8c8d;
    margin-bottom: 3rem;
    font-size: 1.2rem;
  }

  .features-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
  }

  .feature-card {
    text-align: center;
    padding: 2rem;
    border-radius: 10px;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }

  .feature-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  }

  .feature-icon {
    font-size: 3rem;
    margin-bottom: 1rem;
  }

  .feature-card h3 {
    font-size: 1.5rem;
    margin-bottom: 1rem;
    color: #2c3e50;
  }

  .feature-card p {
    color: #7f8c8d;
  }

  /* Featured Pizzas Section */
  .featured-pizzas {
    padding: 5rem 0;
    background-color: #f8f9fa;
  }

  .featured-pizzas h2 {
    text-align: center;
    font-size: 2.5rem;
    margin-bottom: 1rem;
    color: #2c3e50;
  }

