    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: 2rem;
    margin-bottom: 3rem;
  }

  .pizza-card {
    background: white;
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }

  .pizza-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15);
  }

  .pizza-image-container {
    position: relative;
    height: 200px;
    overflow: hidden;
  }

  .pizza-image {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.5s ease;
  }

  .pizza-card:hover .pizza-image {
    transform: scale(1.1);
  }

  .pizza-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(231, 76, 60, 0.8);
    display: flex;
    justify-content: center;
    align-items: center;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .overlay-visible {
    opacity: 1;
  }

  .quick-add-btn {
    background: white;
    color: #e74c3c;
    border: none;
    padding: 0.8rem 1.5rem;
    border-radius: 30px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
  }

  .quick-add-btn:hover {
    background: #f8f9fa;
    transform: scale(1.05);
  }

  .pizza-info {
    padding: 1.5rem;
  }

  .pizza-info h3 {
    font-size: 1.5rem;
    margin-bottom: 0.5rem;
    color: #2c3e50;
  }

  .pizza-info p {
    color: #7f8c8d;
    margin-bottom: 1rem;
  }

  .price-add {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  .price {
    font-size: 1.5rem;
    font-weight: 700;
    color: #e74c3c;
  }

  .add-button {
    background: #e74c3c;
    color: white;
    border: none;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    font-size: 1.5rem;
    cursor: pointer;
    transition: all 0.3s ease;
  }

  .add-button:hover {
    background: #c0392b;
    transform: rotate(90deg);
  }

  .view-all-container {
    text-align: center;
  }

  .view-all-btn {
    background: transparent;
    color: #e74c3c;
    border: 2px solid #e74c3c;
    padding: 0.8rem 2rem;
    border-radius: 30px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
  }

  .view-all-btn:hover {
    background: #e74c3c;
    color: white;
    transform: translateY(-3px);
  }

  /* Process Section */
  .process {
    padding: 5rem 0;
    background: white;
  }

  .process-steps {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin-bottom: 3rem;
  }

  .process-step {
    text-align: center;
    padding: 2rem;
  }

  .step-number {
    font-size: 3rem;
    font-weight: 800;
    color: #f8f9fa;
    text-shadow: -1px -1px 0 #e74c3c, 1px -1px 0 #e74c3c, -1px 1px 0 #e74c3c, 1px 1px 0 #e74c3c;
    margin-bottom: 1rem;
  }

  .process-step h3 {
    font-size: 1.5rem;
    margin-bottom: 1rem;
    color: #2c3e50;
  }

  .process-step p {
    color: #7f8c8d;
  }

  .process-visual {
    display: flex;
    justify-content: center;
    gap: 2rem;
  }

  .chef-image, .oven-image {
    flex: 1;
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  }

  .chef-image img, .oven-image img {
    width: 100%;
    height: 300px;
    object-fit: cover;
    transition: transform 0.5s ease;
  }

  .chef-image:hover img, .oven-image:hover img {
    transform: scale(1.05);
  }

  /* Testimonials Section */
  .testimonials {
    padding: 5rem 0;
    background: #f8f9fa;
  }

  .testimonials-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
  }

  .testimonial-card {
    background: white;
    padding: 2rem;
    border-radius: 15px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  }

  .testimonial-header {
    display: flex;
    align-items: center;
    margin-bottom: 1rem;
  }

  .customer-image {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    overflow: hidden;
    margin-right: 1rem;
  }

  .customer-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .customer-info h4 {
    margin-bottom: 0.3rem;
  }

  .rating {
    color: #f39c12;
  }

  /* Instagram Section */
  .instagram {
    padding: 5rem 0;
    background: white;
  }

  .instagram-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
  }

  .instagram-post {
    position: relative;
    border-radius: 10px;
    overflow: hidden;
    aspect-ratio: 1/1;
  }

  .instagram-post img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.5s ease;
  }

  .instagram-post:hover img {
    transform: scale(1.1);
  }

  .post-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    color: white;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .instagram-post:hover .post-overlay {
    opacity: 1;
  }

  /* Newsletter Section */
  .newsletter {
    padding: 5rem 0;
    background: linear-gradient(135deg, #e74c3c 0%, #c0392b 100%);
    color: white;
    text-align: center;
  }

  .newsletter h2 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
  }

  .newsletter p {
    margin-bottom: 2rem;
    font-size: 1.2rem;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
  }

  .newsletter-form {
    display: flex;
    max-width: 500px;
    margin: 0 auto;
  }

  .newsletter-form input {
    flex: 1;
    padding: 0.8rem 1.2rem;
    border: none;
    border-radius: 30px 0 0 30px;
    font-size: 1rem;
  }

  .newsletter-form button {
    background: #2c3e50;
    color: white;
    border: none;
    padding: 0.8rem 1.5rem;
    border-radius: 0 30px 30px 0;
    font-weight: 600;
    cursor: pointer;
    transition: background 0.3s ease;
  }

  .newsletter-form button:hover {
    background: #1a252f;
  }

  /* Footer */
  .footer {
    background: #2c3e50;
    color: white;
    padding: 3rem 0 1rem;
  }

  .footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin-bottom: 2rem;
  }

  .footer-section h3, .footer-section h4 {
    margin-bottom: 1rem;
    color: #e74c3c;
  }

  .footer-section ul {
    list-style: none;
  }

  .footer-section ul li {
    margin-bottom: 0.5rem;
  }

  .footer-section a {
    color: #ecf0f1;
    text-decoration: none;
    transition: color 0.3s ease;
  }

  .footer-section a:hover {
    color: #e74c3c;
  }

  .social-icons {
    display: flex;
    gap: 1rem;
  }

  .social-icons a {
    font-size: 1.5rem;
  }

  .footer-bottom {
    text-align: center;
    padding-top: 2rem;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
  }

  /* Buttons */
  .btn-primary, .btn-secondary {
    padding: 0.8rem 1.8rem;
    border-radius: 30px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
  }

  .btn-primary {
    background-color: #e74c3c;
    color: white;
    border: none;
  }

  .btn-primary:hover {
    background-color: #c0392b;
    transform: translateY(-3px);
  }

  .btn-secondary {
    background-color: transparent;
    color: white;
    border: 2px solid white;
  }

  .btn-secondary:hover {
    background-color: white;
    color: #e74c3c;
    transform: translateY(-3px);
  }

  /* Responsive Design */
  @media (max-width: 992px) {
    .video-content h1 {
      font-size: 2.5rem;
    }

    .video-content p {
      font-size: 1.2rem;
    }

    .process-visual {
      flex-direction: column;
    }
  }

  @media (max-width: 768px) {
    .nav {
      position: fixed;
      top: 0;
      right: -100%;
      height: 100vh;
      width: 70%;
      background: white;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      transition: right 0.3s ease;
      box-shadow: -5px 0 15px rgba(0, 0, 0, 0.1);
    }

    .nav-open {
      right: 0;
    }

    .mobile-menu-toggle {
      display: flex;
    }

    .newsletter-form {
      flex-direction: column;
    }

    .newsletter-form input {
      border-radius: 30px;
      margin-bottom: 1rem;
    }

    .newsletter-form button {
      border-radius: 30px;
    }

    .video-content {
      width: 90%;
    }

    .video-content h1 {
      font-size: 2rem;
    }
  }
`;

// Inject styles into the document
const styleSheet = document.createElement("style");
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);
